      `" . TABLE_PREFIX . "posthash`
    SET
      `userid`   =  " . $vbulletin->userinfo['userid'] . ",
      `threadid` =  " . $threadinfo['threadid'] . ",
      `dupehash` = '" . $vbulletin->db->escape_string( $vbulletin->GPC['mrgdp_dupehash'] ) . "',
